function Scholarship() {
  return (
    <div className="App">
      <video
        src={videoBg}
        className="book-video"
        autoPlay
        loop
        muted
      ></video>
      <div className="content">
        <h1>Scholarships</h1>
        <ul className="scholarship-list">
          {scholarships.map((scholarship, index) => (
            <li key={index} className="scholarship-item">
              {scholarship.image ? (
                <a href={scholarship.url} target="_blank" rel="noopener noreferrer">
                  <img src={scholarship.image} alt={scholarship.name} className="scholarship-image" />
                </a>
              ) : (
                <a href={scholarship.url} target="_blank" rel="noopener noreferrer">
                  {scholarship.name}
                </a>
              )}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default Scholarship;